import App from "../App";
import pveInfo from "../components/navbar/pveInfo";
import { createReducer, createAction } from "@reduxjs/toolkit";
import { createSlice } from '@reduxjs/toolkit'

const initialState = {
    skills: pveInfo,
};


export const expSlice = createSlice({
    name: "exp",
    initialState,
    reducers: {
        addExperience: (state, action) => {
            const { exp, skillId } = action.payload;
            // Find the skill by id and update the exp property
            const skill = state.find((skill) => skill.id === skillId);
            if (skill) {
                skill.exp += exp;
            }
        },
    },
});
export const { addExperience } = expSlice.actions
export default expSlice.reducer
