import App from "../App";
import pveInfo from "../components/navbar/pveInfo";
import { createReducer, createAction } from "@reduxjs/toolkit";


const initialState = {
    skills: pveInfo,
};

const increaseLevel = createAction("skills/increaseLevel");
const addExperience = createAction("skills/addExperience");

const skillsReducer = createReducer(initialState, (builder) => {
    builder
        .addCase(increaseLevel, (state, action) => {
            const { skillId } = action.payload;
            state.skills = state.skills.map((skill) =>
                skill.id === skillId
                    ? { ...skill, currentLvl: skill.currentLvl + 1 }
                    : skill
            );
        })
        .addCase(addExperience, (state, action) => {
            const { skillId, exp } = action.payload;
            state.skills = state.skills.map((skill) =>
                skill.id === skillId ? { ...skill, exp: skill.exp + exp } : skill
            );
        });
});

export default skillsReducer;
