            // }
            console.log(skillId)
            if (obj.length === 1) {
                console.log(obj[0].exp)
                state.skills[skillId-1].exp += obj[0].exp
            }
            else if (obj.length === 2){
                state.skills[skillId-1].exp += obj[0].exp + obj[0].exp 
            }




        },
