import LinearProgress from '@mui/material/LinearProgress';
import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux"
import { addExperience } from "../../../../store/expReducer";
import progressBar from "../allProf/progressBar";
function WoodProgress() {
    const dispatch = useDispatch()



    const currentAction = useSelector(
        (state) => state.woodCutting.actionArr)
    const [setCurrentAction, curAction] = useState(currentAction)
    console.log(1)
    console.log(currentAction[0])
    console.log(1)
    console.log(curAction)
    const addExp = () => {
        dispatch(addExperience({ exp: currentAction[0].exp, skillId: "1", }));
    };
    useEffect(() => {
        if (currentAction[0]) {
            const interval = setInterval(() => {
                addExp();

            }, currentAction[0].actionTime * 1000);

            return () => {
                clearInterval(interval); // Cleanup the interval on component unmount
            };
        }
    }, [currentAction[0].actionTime]);


    return (
        <div className="wood-progress">
            <div className="wood-progress-animation wood-progress-bar-animation">
                    <div className="wood-progress-bar-animation"
                     style={{
                    animation: curAction[0]
                        ? `progressAnimation ${curAction[0].actionTime}s infinite linear`
                        : "none",
                }}
                >

                </div>
            </div>
            <h1>{currentAction[0] ? currentAction[0].actionTime + " sec" : "Information will be here"} </h1>
        </div>
    );
}

export default WoodProgress;
