import App from "../App";

const defaultState ={
    exp: 5,
}
const cashReducer = (state = defaultState, action) => {
    switch (action.type){
        case "ADD_EXP":
            return {...state, exp: state.exp + action.payload}
        case "SPEND_EXP":
            return {...state, exp: state.exp - action.payload}

        default:
            return state
    }
}
export default cashReducer;
