import { createReducer, createAction } from "@reduxjs/toolkit";
import { createSlice, current  } from '@reduxjs/toolkit'
import xpTable from "../willBeAppi/xpTable";
import profInfo from "../willBeAppi/profInfo";
import WoodBlockReqTemp from "../willBeAppi/WoodBlockReq(temp)";
const initialState = {
    skills: profInfo,
    woodCuttingMastery: WoodBlockReqTemp
};

function masteryLvl(currentExp, Exp, Lvl) {

    return currentExp + Exp < xpTable[Lvl - 1].exp;
}


    export const expSlice = createSlice({
        name: "exp",
        initialState,
        reducers: {
            addExperience: (state, action) => {
                const {obj} = action.payload;
                console.log(10, obj)
                const skillId = 1 // PLACEHOLDER
                let exp = obj[2] ? obj[2].exp : obj[0].exp;
                if (state.skills[skillId - 1].exp + exp < xpTable[state.skills[skillId - 1].currentLvl - 1].exp) {
                    state.skills[skillId - 1].exp += exp
                } else {
                    state.skills[skillId - 1].currentLvl += 1
                    state.skills[skillId - 1].exp += exp
                }
            },
            addMastery: (state, action) => {
                const {obj} = action.payload;
                const skillId = 1 // PLACEHOLDER
                console.log(20, obj)

                if (masteryLvl(state.woodCuttingMastery[obj[0].id - 1].masteryExp, state.woodCuttingMastery[obj[0].id - 1].mastery * 10, state.woodCuttingMastery[obj[0].id].mastery)){
                    state.woodCuttingMastery[obj[0].id - 1].masteryExp += state.woodCuttingMastery[obj[0].id - 1].mastery * 10
                }
                else {
                    state.woodCuttingMastery[obj[0].id - 1].mastery += 1
                    state.woodCuttingMastery[obj[0].id - 1].masteryExp += state.woodCuttingMastery[obj[0].id - 1].mastery * 10
                }
                if (obj.length === 3){
                    if (masteryLvl(state.woodCuttingMastery[obj[1].id - 1].masteryExp, state.woodCuttingMastery[obj[1].id - 1].mastery * 10, state.woodCuttingMastery[obj[1].id].mastery)){
                        state.woodCuttingMastery[obj[1].id - 1].masteryExp += state.woodCuttingMastery[obj[1].id - 1].mastery * 10
                    }
                    else {
                        state.woodCuttingMastery[obj[1].id - 1].mastery += 1
                        state.woodCuttingMastery[obj[1].id - 1].masteryExp += state.woodCuttingMastery[obj[1].id - 1].mastery * 10
                    }
                }

            }
        }

    });

export const { addExperience, addMastery} = expSlice.actions
export default expSlice.reducer
